package main

import (
	"fmt"
	"github.com/tidwall/gjson"
)

const json1 = `{
  "names": 
    {
      "name": "十五",
      "age" : 1
    },
	"animal":[
		{
			"name":"dog",
			"age":3
		},
		{
			"name":"cat",
			"age":5
		}
	]
}`

func main() {
	value1 := gjson.Get(json1, "names.name")
	fmt.Println(value1.String())

	value2 := gjson.Get(json1, "names.age")
	fmt.Println(value2.Int())
	fmt.Println("-----------------------------------------")
	name1 := gjson.Get(json1, "animal.0.name").Str
	fmt.Println("name1:", name1)
	age1 := gjson.Get(json1, "animal.0.age").Int()
	fmt.Println("age1:", age1)

	name2 := gjson.Get(json1, "animal.1.name").Str
	fmt.Println("name2:", name2)
	age2 := gjson.Get(json1, "animal.1.age").Int()
	fmt.Println("age2:", age2)

	name3 := gjson.Get(json1, "animal.2.name").Str
	fmt.Println("name3:", name3)
	age3 := gjson.Get(json1, "animal.2.age").Int()
	fmt.Println("age3:", age3)
	fmt.Println("==================================================================")
	amimals := gjson.Get(json1, "animal")
	fmt.Println("animals:", amimals)
	ty := amimals.Type
	fmt.Println(ty)
	fmt.Println(ty == 5)
}

